import "core-js/stable";
import "regenerator-runtime/runtime";
import { waitForAppScreen, zemu } from './test.fixture';

const BASE_SCREENS_S = (1 + 1 + 1 + 1 + 1) //YEARN + AMOUNT + VAULT + GAS_FEES + APPROVE
const BASE_SCREENS_X = (1 + 1 + 1 + 1 + 1) //YEARN + AMOUNT + VAULT + GAS_FEES + APPROVE

test('[Nano S] CRV Zapper', zemu("nanos", async (sim, eth) => {
  // Original TX: https://etherscan.io/tx/0xe2fb7aebb9df20533a1f2a154ee6267cbb3204d3c44cc0cbf1ad3643ace5c847
  eth.signTransaction(
    "44'/60'/0'/0/0",
    ""
  );

  await waitForAppScreen(sim);
  await sim.navigateAndCompareSnapshots('.', 'nanos_zap_crv', [BASE_SCREENS_S, 0]);
}));


